import { Component, OnInit } from '@angular/core';
import { Empleado } from '../empleado';
import { EmpleadoService } from '../empleado.service';
import { Route, Router } from '@angular/router';

@Component({
  selector: 'app-registrar-empleado',
  templateUrl: './registrar-empleado.component.html',
  styleUrls: ['./registrar-empleado.component.css']
})
export class RegistrarEmpleadoComponent implements OnInit {


  empleado : Empleado = new Empleado();
  constructor(private empleadoServicio:EmpleadoService,private router:Router){}

  ngOnInit(): void{
  }
guardarEmpleado(){
  this.empleadoServicio.registrarEmpleado(this.empleado).subscribe(dato =>{
    console.log(dato);
    this.irALaListaDeEmpleados();
  },error => console.log(error));
}

  irALaListaDeEmpleados(){
    this.router.navigate(['/empleados']);
  }

  onSubmit(){
    this.guardarEmpleado();
  }

}
<div class="col-md-6 offset-md-3">
 <h3 class="text-center">Registro de empleado</h3>

 <form (ngSubmit)="onSubmit()"  #empleadoForm = "ngForm">
    <div class="form-group">
        <label>Nombre:</label>
        <input type="text" class="form-control" id="nombre" [(ngModel)] = "empleado.nombre" name ="nombre">
        <label>Telefono:</label>
        <input type="text" class="form-control" id="telefono" [(ngModel)] = "empleado.telefono" name ="telefono">
    </div>
    <button class="btn btn-success">Guardar</button>
 </form>
</div>
