import { Component, OnInit } from '@angular/core';
import { EmpleadoService } from './../empleado.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Empleado } from '../empleado';

@Component({
  selector: 'app-actualizar-empleado',
  templateUrl: './actualizar-empleado.component.html',
  styleUrls: ['./actualizar-empleado.component.css']
})
export class ActualizarEmpleadoComponent implements OnInit{
 id:number;
 empleado:Empleado = new Empleado();

 constructor(private empleadoService:EmpleadoService,private router:Router,private route:ActivatedRoute) { }

 ngOnInit(): void {
  this.id = this.route.snapshot.params['id'];
  this.empleadoService.obtenerEmpleadoPorId(this.id).subscribe(dato =>{
    this.empleado = dato;
  },error => console.log(error));
}


}
<div class="col-md-6 offset-md-3">
    <h3 class="text-center">Edición de empleado</h3>
    <form (ngSubmit)= "onSubmit()" #empleadoForm = "ngForm">
      <div class="form-group">
        <label>Nombre : </label>
        <input type = "text"
        class="form-control"
        id = "nombre"
        [(ngModel)] = "empleado.nombre"
        name = "nombre"
        required
        minlength="4"
        maxlength="10"
        placeholder="Digite su nombre "
        #nombre = "ngModel"
        >
        <div class="alert alert-danger" *ngIf= "nombre.invalid && (nombre.dirty || nombre.touched)">
          <div *ngIf = "nombre.errors.required">
            Digite su nombre por favor
          </div>
          <div *ngIf = "nombre.errors.minlength">
            El nombre tiene que contener como minimo 4 caracteres
          </div>
          <div *ngIf = "nombre.errors.maxlength">
            El nombre tiene que tener como maximo 10 caracteres
          </div>
        </div>
      </div>
  
  
      <button class="btn btn-success" [disabled]= "!empleadoForm.form.valid">Guardar</button>
    </form>
  </div>
